import React from 'react';
import {PeopleList, PersonDetail, StarshipsList, StarshipDetail} from '../sw-components'
import Row from '../row';


class PeoplePage extends React.Component {
  state = {
    selectedItemId: 1
  }

  onItemSelected = (id) => {
    this.setState({selectedItemId: id})
  }

  render() {
    const itemList = <PeopleList onItemSelected={this.onItemSelected}/>
    const personDetails = <PersonDetail selectedItemId={this.state.selectedItemId} />

    const starshipList = <StarshipsList onItemSelected={this.onItemSelected}/>
    const starshipstarshipDetails = <StarshipDetail selectedItemId={this.state.selectedItemId} />

    return (
      <>
      <Row
        leftComponent={itemList}
        rightComponent={personDetails}
      />
       <Row
        leftComponent={starshipList}
        rightComponent={starshipstarshipDetails}
      />
    </>
    )
  }
}


export default PeoplePage
