import React from 'react';
import {StarshipsList, StarshipDetail} from '../sw-components'
import Row from '../row';


class PlanetsPage extends React.Component {
  state = {
    selectedItemId: 3
  }

  onItemSelected = (id) => {
    this.setState({selectedItemId: id})
  }

  render() {
    const starshipList = <StarshipsList onItemSelected={this.onItemSelected}/>
    const starshipstarshipDetails = <StarshipDetail selectedItemId={this.state.selectedItemId} />

    return (
       <Row
        leftComponent={starshipList}
        rightComponent={starshipstarshipDetails}
      />
    )
  }
}


export default PlanetsPage
